const initialState = {
    data: []    
}
    
  
    const onMove = (state, payload) => {
            const {id, from, to } = payload
            const {data} = state
            const idx = data[from].findIndex((item) => item.id === id)
            const currentItem = data[from][idx]

            return { data : {
                ...data,
                [from] :[
                ...data[from].slice(0, idx),
                ...data[from].slice(idx + 1)
              ] , 
              [to]: [...data[to], currentItem]
            
            }
          }
    }

  const   onMoveGroup = (state, payload) => {
    const {ids, from, to } = payload
      if(ids.length === 0) return  
      ids.map((id)=> this.onMove(state,{id, from, to}))      
  }

  const reducer = (state = initialState, action) => {
  
    switch (action.type) {
        case 'FETCH_DATA':
            return {
                state
            }
        case 'FETCH_DATA_SUCCESS':
            return {
                data: action.payload,
            }
        case 'FETCH_DATA_ERROR':
            return {
                state
            }
      case 'MOVE_ITEM':
        return  onMove(state, action.payload)
      case "MOVE_ITEM_GROUP":
        return onMoveGroup(state, action.payload)
      default:
        return state;
    }
  };
  
  export default reducer;
