import React from 'react';
import './item.css';
const Item = ({ id, title, onMoveLeft, onMoveRight }) => {
  
  const left = onMoveLeft ? null :
  (<button className={left} type="button" onClick={onMoveLeft}>
    <i className="fa fa-angle-left"></i>
  </button>);
  const right = onMoveRight ? null :
  (<button className={right} type="button" onClick={onMoveRight}>
    <i className="fa fa-angle-right"></i>
  </button>);

  return (
    <div className="row">
      <div className="col-md-1"><input type="chekbox" value={id} checked="false"/></div>
      <div className="col-md-7">
        <span>{title}</span>
      </div>
      <div className="col-md-4">
        {left}
        {right}
      </div>
    </div>
  );
};

export default Item;
