import React, {Component} from 'react';

import Item from '../item/item';
import './grid.css';

export default class Grid extends Component {
    
 defaultState = ()=> {
   return {
    checkedItems : [],
    checkAll : false
  }   
}

state = defaultState()

onCheckAll = (e) => {
  const checked = e.target.checked;
  if(checked)
    this.setState((state) => { 
      return {
        checkAll:checked,
        checkedItems:[...this.props.items.map((item)=> (item.id))]
      }
    
    }) 
    else
    {
      this.setState((state) => { 
        return {
          checkAll:checked,
          checkedItems:[],
        }
    })
  }
}

onCheck = (e) => {
  const checked = e.target.checked;
  const id = e.target.value *1;

  if(checked===true)
    this.setState((state) => {

      return {
        checkedItems:[...state.checkedItems, id],
        checkAll: state.checkedItems.length +1 === this.props.items.length
      }
    }) 
  else{
    this.setState((state) => {
      const idx = state.checkedItems.findIndex((item) => item === id)

      return {
        checkedItems:[
        ...state.checkedItems.slice(0, idx),
        ...state.checkedItems.slice(idx + 1)
      ],
      checkAll: false
    
    }
    })
  }
}

onMoveGroup = this.props.onMoveGroup

onMoveLeftGroup=  () =>{
  onMoveGroup(this.state.checkedItems, keyName, -1)

  this.setState(() => (this.defaultState()) )
}

onMoveRightGroup = () => {
  onMoveGroup(this.state.checkedItems, keyName, 1)
  this.setState(() => (this.defaultState()) )
}

render() {
  
  
  const { items, keyName , onMove, first, last }= this.props; 
  
  const elements = items.map((item) => {
      const { id, ...itemProps } = item;
      const onMoveLeft = () => {onMove(id, keyName, -1)};
      const onMoveRight = () => {onMove(id, keyName, 1)};
      const checked = this.state.checkAll || this.state.checkedItems.indexOf(id) >-1
      return (
          <div key={id} className="list-group-item">
            <Item
              { ...itemProps }
              onMoveLeft={ onMoveLeft  }
              onMoveRight={ onMoveRight }
              first={first}
              last = {last}
              id={id}
              onCheck={this.onCheck}
              Checked = {checked}
              />
          </div>
      );
  });

  const left = first ? null :
  (<button  type="button" onClick={this.onMoveLeftGroup}>
    <i className="fa fa-angle-left"></i>
  </button>);

  const right = last ? null :
  (<button  type="button" onClick={this.onMoveRightGroup}>
    <i className="fa fa-angle-right"></i>
  </button>);

  return (
  <div className="col-md-3">
    <div className="row title"><b>{keyName} Page</b></div>
    <div className="row title ">
      <div className="col-md-1"><input type= "checkbox" checked={this.state.checkAll} onChange={this.onCheckAll} ></input></div>
      <div className="col-md-6">Name</div>
      <div className="col-md-4">
      {left}
      {right}
      </div>

      
      </div>
    <div className="list-group">{ elements }</div>
  </div>
  )
}
}
