import React from 'react';
import './item.css';
const Item = ({ id, title, onMoveLeft, onMoveRight, first, last }) => {
  
  

  const left = first ? null :
  (<button  type="button" onClick={onMoveLeft}>
    <i className="fa fa-angle-left"></i>
  </button>);

  const right = last ? null :
  (<button  type="button" onClick={onMoveRight}>
    <i className="fa fa-angle-right"></i>
  </button>);

  return (
    <div className="row">
      <div className="col-md-1"><input type="checkbox" value={id}/></div>
      <div className="col-md-6">
        <span>{title}</span>
      </div>
      <div className="col-md-4">
        {left}
        {right}
      </div>
    </div>
  );
};

export default Item;
