import React, {Component} from 'react'
import                         './item.css'
 
export default class Item extends Component {
  static defaultProps =  
  {
    onMoveLeft : () => {},
    onMoveRight: () => {}
  }
  render() { 
    const { id, title, onMoveLeft, onMoveRight } = this.props
    const left = onMoveLeft === this.defaultProps.onMoveLeft ? null :
    (<button type="button" onClick={onMoveLeft}>
      <i className="fa fa-angle-left"></i>
    </button>)
    const right = onMoveRight === this.defaultProps.onMoveLeft ? null :
    (<button type="button" onClick={onMoveRight}>
      <i className="fa fa-angle-right"></i>
    </button>)

    return (
   
    <div className="row">
      <div className="col-md-1"><input type="checkbox" value={id} /></div>
      <div className="col-md-6">
        <span>{title}</span>
      </div>
      <div className="col-md-4">
        {left}
        {right}
      </div>
    </div>
  )
  }
}
