import React, { Component } from 'react';

import AppHeader from '../header/header';

import Grid from '../grid/grid';


import './app.css';


export default class App extends Component {
    state = {
        Left: [
            {id: 0, title: 'Left 0'},
            {id: 1, title: 'Left 1'},
            {id: 2, title: 'Left 2'},
        ],
        Middle: [
            {id: 3, title: 'Middle 0'},
            {id: 4, title: 'Middle 1'},
            {id: 5, title: 'Middle 2'},
        ],
        Right: [
            {id: 6, title: 'Right 0'},
            {id: 7, title: 'Right 1'},
            {id: 8, title: 'Right 2'},
        ]
    };

    onMove(key, id, direction){console.log(key, id, direction)};
    keys = Object.keys(this.state);
    
    render() {
        
        const elements = this.keys.map((key, index) => {
            let first = index === 0;
            let last = index === this.keys.length -1;

            return (
                <div>
                <Grid 
                onMove={this.onMove}
                items= {this.state[key]}
                key={key}
                keyName = {key}
                first = {first}
                last = {last}/>

<span>{first}</span>
            <span>{last}</span>
                </div>
            );
          });


        return  (
        <div className="row">
            
           {elements}
        </div>
        );
          
      };
}
