const requestDataReceived = (items) => {
    return {
        type: 'FETCH_DATA_SUCCESS',
        payload: items
    }
}

const requestDataSend = () => {
    return {
        type: 'FETCH_DATA'
    }
}



const onMove = props => {
    return {
        type: 'MOVE_ITEM',
        payload: props
    }
}

const onMoveGroup = props => {
    return {
        type: 'MOVE_ITEM_GROUP',
        payload: props
    }
}

const fetchData = (dataService, dispatch) => () => {
    dispatch(dataRequested())
    dataService.getData()
        .then((data) => dispatch(dataLoaded(data)))
        .catch((err) => dispatch(dataError(err)))
}

export {
    fetchData,
    onMove,
    onMoveGroup
}
